                console.log('User input:', input.val());
                console.log('Converted value:', userInputCaretNotation);
                console.log('Parsed value:', value);
                console.log('Minimum value:', min);
                console.log('Maximum value:', max);
                var resultElement = input.closest('.d-flex').find('#test_result'); 
                
                if (!isNaN(min) && value < min) {
                    input.css('color', 'red');
                    resultElement.val('Fail');
                    resultElement.css('color', 'red');
                    $('#remarks').prop('required', true);
                } 
                else if (!isNaN(max) && value > max) {
                    input.css('color', 'red');
                    resultElement.val('Fail');
                    resultElement.css('color', 'red');
                    $('#remarks').prop('required', true);
                } 
                else if (!isNaN(max) && !isNaN(min) && value >= min && value <= max) {
                    input.css('color', ''); // Remove red color
                    resultElement.val('Pass');
                    resultElement.css('color', 'green');
                    $('#remarks').prop('required', false);
                }
                else {
                    input.css('color', ''); // Remove red color
                    resultElement.val('');
                }
            });

        
