function encrypt($id)
{
	global $api_key;
	global $api_iv;

	// Encrypt
	$api_encrypted = openssl_encrypt($id, 'aes-256-cbc', $api_key, OPENSSL_RAW_DATA, $api_iv);

	// URL-safe encoding
	$api_encoded = urlencode(base64_encode($api_iv . $api_encrypted));

	return $api_encoded;
}


//FILE UPLOAD VALIDATION
function fileValidation($type, $file, $path)
{
	// Type
	// 0 - General / Exclude invalid file type
	// 1 - Accept Image Only

	$path = 'uploads/' . $path;
	$files = addslashes($_FILES[$file]['name']);
	$size = $_FILES[$file]['size'];
	$tmp = addslashes($_FILES[$file]['tmp_name']);

	$finfo = finfo_open(FILEINFO_MIME_TYPE);
	if (!$finfo) {
		return false; // If finfo_open fails, consider the file invalid
	}
	$mime = finfo_file($finfo, $tmp);
	finfo_close($finfo);

	// $mime = mime_content_type($tmp);
	$mime_array = array(
		'text/x-php',
		'text/plain',
		'text/css',
		'text/html',
		'text/xml',
		'text/javascript',
		'font/ttf',
		'font/woff',
		'font/woff2',
		'application/json',
		'application/x-httpd-php',
		'application/rtf',
		'application/x-sh',
		'application/xhtml+xml',
		'application/xml',
		'application/atom+xml',
		'application/vnd.mozilla.xul+xml',
		'application/x-msdownload'
	);
	if ($type == 1) {
		$mime_array = array('image/png', 'image/jpeg', 'image/jpg');
	}

	$ext_array = array('php', 'php3', 'php4', 'php5', 'phtml', 'cgi', 'pl', 'sh', 'py', 'rb', 'exe', 'dll');
	$ext = strtolower(pathinfo($files, PATHINFO_EXTENSION));

	$files = rand(1000, 1000000) . ' - ' . $files;
	$path = $path . $files;

	$output = array(
		"valid" => false,
		"files" => $files,
		"size" => $size,
		"mime" => $mime
	);
	if (($type == 0 and !in_array($mime, $mime_array)) or ($type == 1 and in_array($mime, $mime_array))) {
		if (move_uploaded_file(stripcslashes($tmp), stripcslashes($path))) {
			$output['valid'] = true;
		}
	}

	return $output;
}

function employerID($ID)
{
	global $conn;

	$selectUser = mysqli_query($conn, "SELECT * from tbl_user WHERE ID = $ID");
	$rowUser = mysqli_fetch_array($selectUser);
	$current_userEmployeeID = $rowUser['employee_id'];

	$current_userEmployerID = $ID;
	if ($current_userEmployeeID > 0) {
		// $selectEmployer = mysqli_query( $conn,"SELECT * FROM tbl_hr_employee WHERE suspended = 0 AND status = 1 AND ID=$current_userEmployeeID" );
		$selectEmployer = mysqli_query($conn, "SELECT * FROM tbl_hr_employee WHERE ID=$current_userEmployeeID");
		if (mysqli_num_rows($selectEmployer) > 0) {
			$rowEmployer = mysqli_fetch_array($selectEmployer);
			$current_userEmployerID = $rowEmployer["user_id"];
		}
	}

	return $current_userEmployerID;
}
function employeeID($ID)
{
	global $conn;

	$selectUser = mysqli_query($conn, "SELECT * from tbl_user WHERE ID = $ID");
	$rowUser = mysqli_fetch_array($selectUser);
