}

function renderComments($conn, $capaId, $type)
{
	$typeEsc = mysqli_real_escape_string($conn, $type);
	$capaId  = (int)$capaId;

	$query = mysqli_query(
		$conn,
		"SELECT c.comments, u.first_name AS name, c.created_at AS date
         FROM tbl_user u 
         LEFT JOIN tbl_cam_comments_remarks c ON u.ID = c.portal_user 
         WHERE c.capa_id = {$capaId}
           AND c.type = '{$typeEsc}'"
	);

	if (!$query || mysqli_num_rows($query) === 0) {
		return;
	}

	while ($row = mysqli_fetch_assoc($query)) {
		$comment = htmlspecialchars($row['comments']);
		$name    = htmlspecialchars($row['name']);
		$timestamp = strtotime($row['date']);
		$date = date(
			(date("Y", $timestamp) === date("Y") ? "d M, h:i A" : "d M Y, h:i A"),
			$timestamp
		);
		$date 	= date("d M, h:i A", strtotime($row['date']));

		echo '
				<li>
					<div class="col1">
							<div class="cont">
									<div class="cont-col2">
											<div class="desc" style="margin-left: 15px;">
													<span class="desc"> ' . $comment . ' </span>
													<span class="desc text-success" style="margin-left: 2em;">@' . $date . ' </span>
											</div>
									</div>
							</div>
					</div>
					<div class="col2">
							<div class="date" style="font-size: 13px;"> ' . $name . '</div>
					</div>
			</li>';
	}
}

